//Used ChatGPT for help

import React from 'react';
import { Form } from 'react-bootstrap';
import { BsStarFill, BsStarHalf, BsStar } from 'react-icons/bs';

const Star = ({ rating, onChange }) => {
    const stars = [1, 2, 3, 4, 5];

    return (
        <div className="star-rating">
        <span className="star-rating__title">Rating:</span>
        {stars.map((star) => (
          <label key={star} className="star-rating__star">
            <input
              type="checkbox"
              name="rating"
              value={star}
              checked={star <= rating}
              onChange={() => onChange(star)}
            />
            <span className="star-rating__icon">
              {star <= rating ? (
                <BsStarFill className="text-warning" />
              ) : star - 0.5 === rating ? (
                <BsStarHalf className="text-warning" />
              ) : (
                <BsStar className="text-warning" />
              )}
            </span>
          </label>
        ))}
      </div>
    //      <div className="star-rating">
    //   <span className="star-rating__title">Rating:</span>
    //   {[1, 2, 3, 4, 5].map((star) => (
    //     <label key={star} className="star-rating__star">
    //       <input
    //         type="radio"
    //         name="rating"
    //         value={star}
    //         checked={rating === star}
    //         onChange={() => onChange(star)}
    //       />
    //       <span className="star-rating__icon"></span>
    //     </label>
    //   ))}
    // </div>

   );
  };

export default Star;
