import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Comment from './Comment';
import Titles2 from './Tiles2';
function ComCard({name,photo,desc,title,rev1}) {
    return (
      <Card
      style={{
        border: 'solid 3px',
        maxWidth: 'auto',
        borderRadius: '10px',
        backgroundColor: '#cee6fe',
        height: '275px',
      }}
    >
      <Card.Body>
      <div data-bs-spy="scroll" tabindex="0">
        <Row>
          <Col xs={4}>
            <img
              src={photo}
              style={{ maxWidth: '100%', height: 'auto' }}
            />
          </Col>
          <Col xs={8}>
          {title && <Titles2 word={name} />}
            <p>{desc}</p>
          {rev1 && <hr style={{ borderTop: '1px solid #999999', marginTop: '10px' }} />}
          {rev1 && <p>{desc}</p>}
          {rev1 && <Comment/>}
          </Col>
        </Row>
        </div>
      </Card.Body>
    </Card>  
    );
}
  
  export default ComCard;
