//Used ChatGPT for help

import React from 'react';
import { Form } from 'react-bootstrap';
import { BsStarFill, BsStarHalf, BsStar } from 'react-icons/bs';

const Star = ({ rating, onChange }) => {
    const stars = [1, 2, 3, 4, 5];
    const handleStarClick = (selectedRating) => {
        if (rating === selectedRating) {
          // If the same star is clicked again, set the rating to 0
          onChange(0);
        } else {
          onChange(selectedRating);
        }
    };
    return (
        <div className="star-rating">
        <span className="star-rating__title">Rating:</span>
        {[...Array(maxRating)].map((_, index) => {
          const starValue = index + 1;
          return (
            <Form.Label key={starValue}>
              <input
                type="radio"
                name="rating"
                value={starValue}
                checked={starValue <= rating}
                onChange={() => handleStarClick(starValue)}
                className="star-rating__input visually-hidden"
              />
              <span className="star-rating__icon">
                {starValue <= rating ? (
                  <BsStarFill className="text-warning" />
                ) : starValue - 0.5 === rating ? (
                  <BsStarHalf className="text-warning" />
                ) : (
                  <BsStar className="text-warning" />
                )}
              </span>
            </Form.Label>
          );
        })}
      </div>
    //      <div className="star-rating">
    //   <span className="star-rating__title">Rating:</span>
    //   {[1, 2, 3, 4, 5].map((star) => (
    //     <label key={star} className="star-rating__star">
