import Form from 'react-bootstrap/Form';
import React, { useState } from 'react';
import profilepic from './empty-profile.jpg';
import UploadImage from './UploadImage';


function PagesName({addName}) {
  const [name, setName] = useState('');
  const [city, setCity] = useState('');

  const onSubmit = (e) => {
    e.preventDefault()

    if(!name) 
    {
      alert('Please add a name')
      return
    }

    addName({name, city})

    setName('')
    setCity('')
  }


    return (
      <form className="add-form" onSubmit={onSubmit}>
        <div className="col">
          <div className="row">
            <Form style={{width: 320,}}>
                <Form.Group className="mb-3">
                  <Form.Label>Change Display Name:</Form.Label>
                  <Form.Control type="name" placeholder="Display Name" />
                </Form.Group>
            </Form>
          </div>

          <div className="row">
            <Form style={{width: 320,}}>
                <Form.Group className="mb-3">
                  <Form.Label>Your Location:</Form.Label>
                  <Form.Control type="city" placeholder="Name of your City" />
                </Form.Group>
            </Form>
          </div>
        </div>

        <div className="col">
          <div className="row">
            <UploadImage/>
          </div>

          <div className="row"> 
            <button className="btn-add-prof-pic">Add Profile Picture</button>
          </div>
        </div>

      </form>
    );
  }
  
export default PagesName;
