import Form from 'react-bootstrap/Form';
import React, { useState } from 'react';
import profilepic from './empty-profile.jpg';


function PagesName({addName}) {
  const [name, setName] = useState('');
  const [city, setCity] = useState('');

  const onSubmit = (e) => {
    e.preventDefault()

    if(!name) 
    {
      alert('Please add a name')
      return
    }

    addName({name, city})

    setName('')
    setCity('')
  }


    return (
      <>
        <div className="col">
          <div className="row">
            <Form style={{width: 320,}} onSubmit={onSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Change Display Name:</Form.Label>
                  <Form.Control type="Username" placeholder="Display Name" />
                </Form.Group>
            </Form>
          </div>

          <div className="row">
            <Form style={{width: 320,}} onSubmit={onSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Your Location:</Form.Label>
                  <Form.Control type="Location" placeholder="Name of your City" />
                </Form.Group>
            </Form>
          </div>
        </div>

        <div className="col">
          <div className="row">
            <img src={profilepic} alt="profilepic" style={{width: 350, height: 200,}}/>
          </div>

          <div className="row">
            <button className="btn-add-prof-pic">Add Profile Picture</button>
          </div>
        </div>

      </>
    );
  }
  
export default PagesName;
