import Form from 'react-bootstrap/Form';
import React, { useState } from 'react';



function PagesName({onAdd}) {
  const [name, setName] = useState('');


  const onSubmit = (e) => {
    e.preventDefault()

    if(!name) 
    {
      alert('Please add a name')
      return
    }

    onAdd({name})
    setName('')
  }


    return (
      <>
        <div className="row" onSubmit={onSubmit}>
            <Form style={{width: 320,}}>
                <Form.Group className="mb-3">
                  <Form.Label>Change Display Name:</Form.Label>
                  <Form.Control type="name" placeholder="Display Name" value={name} onChange={(e) => setName(e.target.value)}/>
                </Form.Group>
            </Form>
          </div>

        
        
      </>
    );
  }
  
export default PagesName;


<form className="add-form" onSubmit={onSubmit}>
          <div className="form-control">
            <label>Name</label>
            <input type="name" placeholder="Add Name" value={name} onChange={(e) => setName(e.target.value)}/>
          </div>

          <input type="submit" value = "Save"/>
        </form>
